import React, { useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import { v4 as uuid } from 'uuid';
import esLocale from '@fullcalendar/core/locales/es';


const MyCalendar = () => {
  const [events, setEvents] = useState([]);

  // Agregar el evento de cumpleaños de Marta
  const addBirthdayEvent = () => {
    const birthdayDate = new Date('2023-11-05'); // Fecha del cumpleaños de Marta
    const eventName = 'Cumpleaños de Marta';

    const newEvent = {
      start: birthdayDate,
      title: eventName,
      id: uuid(),
    };

    setEvents([...events, newEvent]);
  };

  // Obtén la fecha actual
  const currentDate = new Date();

  // Función para obtener el título personalizado del encabezado
  const getHeaderTitle = (date) => {
    const options = { year: 'numeric', month: 'long' };
    return date.toLocaleDateString('es-ES', options);
  };

  return (
  <div className='calendar'>
    <FullCalendar
      editable
      selectable
      events={events}
      initialDate={currentDate}
      headerToolbar={{
        start: 'today prev,next', // Muestra las flechas para cambiar de mes
        center: 'title', // Usa la función personalizada para mostrar el título del mes actual
        end: 'dayGridMonth dayGridWeek dayGridDay',
      }}
      plugins={[dayGridPlugin, interactionPlugin]}
      views={{
        dayGridMonth: {
          titleFormat: { year: 'numeric', month: 'long' }, // Muestra el nombre completo del mes
        },
      }}
      locale={esLocale} // Establece el idioma en español
      dateClick={(info) => {
        // Llama a la función para agregar el cumpleaños de Marta cuando se hace clic en una fecha
        addBirthdayEvent();
      }}
    />
  </div>
);

};

export default MyCalendar;
