import React, { useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import { v4 as uuid } from 'uuid';

const MyCalendar = () => {
  const [events, setEvents] = useState([]);
  const handleSelect = (info) => {
    const { start, end } = info;
    const eventName = "Feria de Málaga";
    const eventStart = new Date("2023-10-08T08:00:00");
    const eventEnd = new Date("2023-10-08T20:00:00");

    const newEvent = {
      start: eventStart,
      end: eventEnd,
      title: eventName,
      id: uuid(),
    };

    setEvents([...events, newEvent]);
  };

  // Obtén la fecha actual
  const currentDate = new Date();

  // Función para obtener el título personalizado del encabezado
  const getHeaderTitle = (date) => {
    const options = { year: 'numeric', month: 'long' };
    return date.toLocaleDateString('es-ES', options);
  };

  return (
    <div className='calendar'>
      <FullCalendar
        editable
        selectable
        events={events}
        select={handleSelect}
        initialDate={currentDate}
        headerToolbar={{
          start: 'customHoy prev,next', // Utiliza 'customHoy' en lugar de 'today'
          center: 'title',
          end: 'dayGridMonth dayGridWeek dayGridDay',
          customButtons: {
            customHoy: {
              text: 'Hoy',
              click: function() {
                // Agrega aquí el código para manejar el clic en el botón "Hoy"
                // Por ejemplo, puedes hacer que el calendario muestre la fecha actual.
                const today = new Date();
                // Usa la función para ir a la fecha actual
                MyCalendar.gotoDate(today);
              }
            }
          }
        }}
        plugins={[dayGridPlugin, interactionPlugin]}
        views={{
          dayGridMonth: {
            titleFormat: { year: 'numeric', month: 'long' }
          }
        }}
      />
    </div>
  );
};

export default MyCalendar;
