import FullCalendar from '@fullcalendar/react';
import daygridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import { useState } from 'react';
import { v4 as uuid } from 'uuid';

const MyCalendar = () => {
  // el array con todos los eventos que vas añadiendo al calendario
  const [events, setEvents] = useState([]);
  // esta es la funcion que se va a ejecutar para añadir eventos
  const handleSelect = (info) => {
  const { start, end } = info;
  const eventName = "Feria de Málaga";
  const eventStart = new Date("2023-10-08T08:00:00"); // Fecha y hora de inicio
  const eventEnd = new Date("2023-10-08T20:00:00"); // Fecha y hora de fin

  const newEvent = {
    start: eventStart,
    end: eventEnd,
    title: eventName,
    id: uuid(),
  };

  setEvents([...events, newEvent]);
};

  return (
    <div className='calendar'>
      <FullCalendar
        editable
        selectable
        events={events}
        select={handleSelect}
        // configuracion del calendarios, si quieres puedes quitar estos botones

        headerToolbar={{
          start: 'today prev next',
          end: 'dayGridMonth dayGridWeek dayGridDay',
        }}
        plugins={[daygridPlugin, interactionPlugin]}
        views={['dayGridMonth', 'dayGridWeek', 'dayGridDay']}
      />
    </div>
  );
};

export default MyCalendar;
