import React, { useState, useEffect } from 'react';
import fotoPortada from '../images/mueble-portada.jpeg';
import MyCalendar from '../components/calendar/Calendar';
import MyProducts from '../components/products/Products';
import WhoWeAre from '../components/whoweare/WhoWeAre';
import Menu from '../components/menu/Menu';
import '../styles/App.css';


function App() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };
  //cerrar el menú cuando se hace clic en un enlace del menú
  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <div className="app">
      <Menu/>
      <main className="main">
        <section className="main-section">
          <img className="image" src={fotoPortada} alt="Foto portada" />
        </section>
          <MyProducts/>
          <WhoWeAre/>
        <section id="contacta" className="main-section">
          <form className="form">
            <h2>Contacta</h2>
            <label>
              Tu nombre completo:
              <input type="text" name="nombre" />
            </label>
            <br />
            <label>
              Correo electrónico:
              <input type="email" name="email" />
            </label>
            <br />
            <label>
              ¿Qué nos quieres preguntar?
              <textarea name="mensaje"></textarea>
            </label>
            <br />
            <button class="submit-button" type="submit">Enviar</button>
          </form>
        </section>
          <MyCalendar/>
      </main>

      <footer className="footer">
        <p>Contacto: info@blablabla.com</p>
        <p>Ubicación: Manresa, Barcelona</p>
        <a href="#">Instagram</a>
        <a href="#">Todo Colección</a>
      </footer>
    </div>
  );
}

export default App;
